import {
    LaptopOutlined,
    NotificationOutlined,
    UserOutlined,
  } from "@ant-design/icons";
  import { Breadcrumb, Layout, Menu, theme } from "antd";
  import React from "react";
  import { Outlet } from "react-router-dom";
  import MenuEdit from "../../components/menu";
  
  const { Header, Content, Sider, Footer } = Layout;
  
  const Index = (props) => {
    return (
      <Layout>
        <Header
          className="header"
          style={{ backgroundColor: "white", textAlign: "center" }}
        >
          <div className="logo" style={{ fontSize: "24px", fontWeight: "bold" }}>
            Ngân hàng câu hỏi
          </div>
        </Header>
        <Layout>
          <Sider
            width="auto"
            style={{
              background: "#ffff",
            }}
          >
            <MenuEdit />
          </Sider>
          <Layout
            style={{
              padding: "0 24px 24px",
              paddingBottom: "0px",
              height: "850px",
            }}
          >
            {/* header content */}
            {/* <h3>Home</h3>
  
            <Content
              style={{
                padding: 24,
                margin: 0,
                minHeight: 280,
                background: "#ffff",
              }}
            >
              Content
            </Content> */}
            <Outlet />
            <Footer style={{ textAlign: "center", padding: "10px 50px" }}>
              ©2023 Created by TieuPhong
            </Footer>
          </Layout>
        </Layout>
      </Layout>
    );
  };
  
  export default Index;
