import { Modal, Button, Form, Input, Select } from 'antd';
import { useState } from 'react';
import { useEffect } from "react";
import api from "../../api/config"

const Index = ({
    visible,
    dataEdit,
    action,
    onCancel,
    onSubmit
}) => {
    const [form] = Form.useForm();
    const { Option } = Select;
    const [DanhSachThamSo, setDanhSachThamSo] = useState([])

    useEffect(() => {
        if (action == "edit" && dataEdit.Id_ThamSo) {
            console.log(dataEdit)
            form.setFieldsValue({
                ...dataEdit
            })
        }

        api.DanhSachThamSo().then(res => {
            if (res.data.Status === 1) {
                setDanhSachThamSo(res.data.Data)
            }
        })
    }, [dataEdit])

    const onReset = () => {
        form.resetFields();
    };

    const onFinish = (values) => {
        console.log('Success:', values);
    };
    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    const onOk = async () => {
        const values = await form.validateFields()
        onSubmit(values)
        onReset()
    }

    return (
        <Modal
            title={action == "add" ? "Thêm mới tham số" : "Sửa tham số"}
            visible={visible}
            onOk={onOk}
            onCancel={onCancel}
            footer={[
                <Button key="back" onClick={onCancel}>
                    Thoát
                </Button>,
                <Button key="submit" type="primary" onClick={onOk}>
                    {action == 'add' ? "Thêm mới" : "Cập nhật"}
                </Button>,
            ]}
        >
            <Form
                form={form}
                name="basic"
                labelCol={{
                    span: 6,
                }}
                wrapperCol={{
                    span: 18,
                }}
                initialValues={{
                    remember: true,
                }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                autoComplete="off"
            >
                {
                    action == "edit" && <Form.Item
                        label="ID"
                        name="Id_ThamSo"
                        hidden
                    >
                        <Input />
                    </Form.Item>
                }
                <Form.Item
                    label="Tên tham số"
                    name="NameThamSo"
                    rules={[
                        {
                            required: true,
                            message: 'Vui lòng nhập thông tin!',
                        },
                    ]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    label="Giá trị"
                    name="GiaTri"
                    rules={[
                        {
                            required: true,
                            message: 'Vui lòng nhập thông tin!',
                        },
                    ]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    label="Ghi Chú"
                    name="GhiChu"
                    rules={[
                        {
                            required: true,
                            message: 'Vui lòng nhập thông tin!',
                        },
                    ]}
                >
                    <Input />
                </Form.Item>

            </Form>
        </Modal>
    )
}

export default Index;
