import {
    MailOutlined,
    SettingOutlined,
    AppstoreOutlined,
  } from "@ant-design/icons";
  import { useState } from "react";
  import { Layout, Menu } from "antd";

// import Link from "antd/es/typography/Link";
import { Link } from 'react-router-dom'
  const { Sider } = Layout;
  function getItem(label, key, icon, children, type) {
    return {
      key,
      icon,
      children,
      label,
      type,
    };
  }
  const items = [
    getItem(<b>Quản lý hệ thống</b> , "sub4", <SettingOutlined />  , [
        getItem("Quản lý người dùng", "9"),
        getItem("Quản lý chức năng", "10"),
        getItem("Phân Quyền", "11"),
        getItem("Quản lý cấu hình", "12"),
        getItem(<Link to="tham-so">Quản lý hệ thống</Link>, "13"),
      ]),
    getItem("Quản lý danh mục", "sub2", <AppstoreOutlined />, [
      getItem(<Link to="bac-hoc">Danh mục bậc học</Link>, "5"),
      getItem("Danh mục trường", "6"),
      getItem("Danh mục khối học", "5"),
      getItem("Danh mục môn học", "6"),
      
    ]),
    getItem("Ngân Hàng Câu Hỏi" , "sub1", <MailOutlined />, [
        getItem("Quản lý mức độ câu hỏi", "1"),
        getItem("Quản lý nhãn câu hỏi", "2"),
        getItem("Biên soạn ngân hàng câu hỏi", "3"),
        getItem("Quản lý ma trận đề", "4"),
      ]),
  ];
  
  // submenu keys of first level
  const rootSubmenuKeys = ["sub1", "sub2", "sub4"];
  const Index = () => {
    const [openKeys, setOpenKeys] = useState(["sub1"]);
    const onOpenChange = (keys) => {
      const latestOpenKey = keys.find((key) => openKeys.indexOf(key) === -1);
      if (rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
        setOpenKeys(keys);
      } else {
        setOpenKeys(latestOpenKey ? [latestOpenKey] : []);
      }
    };
    return (
      <Sider
        width="auto"
        style={{
          background: "#ffff",
        }}
      >
        <div style={{ padding : " 0 28px", fontWeight: "bold"}}>
          Dashbloard
        </div>
        <Menu
          mode="inline"
          openKeys={openKeys}
          onOpenChange={onOpenChange}
          style={{
            width: 266,
          }}
          items={items}
        />
      </Sider>
    );
  };
  export default Index;
