import React from 'react';

import { Breadcrumb, Layout, Menu, theme, Table } from 'antd';
const { Header, Content, Sider } = Layout;
const columns = [
  {
    title: "STT",
    align: "center",
    render: (_, record, index) => index + 1,
  },
  {
    title: "Tham số",
    dataIndex: "ThamSo",
    key: "ThamSo",
    align: "left",
    border: "1px solid #f0f0f0",
  },
  {
    title: "Giá trị",
    dataIndex: "GiaTri",
    key: "GiaTri",
    align: "left",
  },
  {
    title: "Ghi chú",
    dataIndex: "GhiChu",
    key: "GhiChu",
    align: "left",
  },

  {
    title: "Thao tác",
    align: "center",
    render: (_, record) => renderThaoTac(record),
  },
];

function index(props) {
  return (
    <div>

      {"  "}
      <h5>QUẢN LÝ HỆ THỐNG</h5>
      <Content
        style={{
          padding: 24,
          margin: 0,
          minHeight: 280,
          background: "#ffff",
        }}
      >

        <Table
          rowKey={(record) => record.id}
          // columns={columns}
          // dataSource={DanhSachThamSoHeThong}
          bordered
        />
      </Content>

    </div>);
}

export default index;
