import React from 'react';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
const { Header, Content, Sider } = Layout;

function index(props) {
  return (
    <div>

      {"  "}
      <h5>QUẢN LÝ HỆ THỐNG</h5>
      <Content
        style={{
          padding: 24,
          margin: 0,
          minHeight: 280,
          background: "#ffff",
        }}
      >

        Quản lý cấu hình
        <Wrapper>
          <BoxHeader>
            <BoxTitle>
              Quản lý cấu hình
            </BoxTitle>

            <Button type="primary" onClick={showModalAdd}>
              Thêm mới
            </Button>
          </BoxHeader>
          <Box>
            <Table
              rowKey={(record) => record.Id_ThamSo}
              columns={columns}
              dataSource={DanhSachThamSo}
            />
          </Box>
          <ModalAddEdit
            visible={visible}
            action={action}
            dataEdit={dataEdit}
            onSubmit={handleSubmit}
            onCancel={hiddenModal}
          />
        </Wrapper>
      </Content>

    </div>);
}

export default index;
